    function Remover (){
        setValor(valor - 1)
    }

    return(
        <div className="card">
        <div className="card-header">
          Meu primeiro Card
        </div>
            <div className="card-body">
          
          
                <p>{valor}</p>
            </div>
      </div>
    )
}
